import React from "react";
import './App.css';
import axios from 'axios';
import { useState, useEffect } from 'react';
import { Layout, Divider } from 'antd';
import { Input } from 'antd';
import { Spin } from 'antd';
import { Image } from 'antd';
import { Button, Radio } from 'antd';
import {
  Link,
  HashRouter
} from "react-router-dom";


//const { Link } = Anchor;
const { Content, Footer } = Layout;
const { Search } = Input;

const api = axios.create({
  baseURL: 'https://api.nytimes.com/'
})

const Home = () => {

  const [input, setInput] = useState("");
  const [inputSection, setInputSection] = useState("");

  const [period, setPeriod] = useState(1);

  let [news, setNews] = useState([]);

  const noNews = !news || (news && news.length === 0);

  const getNews = async (period) => {
    const response = await api.get(`/svc/mostpopular/v2/viewed/${period}.json?api-key=AA45vZHuAJQGg0hEHqL3Skr1m1YH7V2H`).catch((err) => console.log("Error:", err));
    //console.log(period);
    //console.log(response.data.results);

    if (response && response.data) {
      setNews(response.data.results);
      //console.log(response.data);
    }
  }

  const handleChange = (e) => {
    e.preventDefault();
    setInput(e.target.value);
  }

  const handleChangeSection = (e) => {
    e.preventDefault();
    setInputSection(e.target.value);
  }

  if (inputSection.length > 0) {
    news = news.filter((i) => {
      return i.section.toLowerCase().match(inputSection);
    })
  }

  async function handlePeriodChange(e) {
    //console.log(e.target.value);
    await getNews(e.target.value);
  }

  if (input.length > 0) {
    news = news.filter((i) => {
      return i.title.toLowerCase().match(input);
    })
  }

  function storeData(news){
    console.log(news);
    sessionStorage.setItem("title", news.title);
    sessionStorage.setItem("image", news.media?.[0]?.['media-metadata']?.[2]?.url);
    sessionStorage.setItem("abstract", news.abstract);
    sessionStorage.setItem("url", news.url);
  }

  useEffect(() => {
    getNews(period);
    console.log('this is useEffect');
  }, [])

  function ImageDemo() {
    return (
      <Image
        width={200}
        height={200}
        src="error"
        fallback="data:image/png;base64,"
      />
    );
  }

  function CheckImage(news){
    if(news.media[0] == undefined){
      return (
        <Image
          width={200}
          height={200}
          src="error"
          fallback="data:image/png;base64,"
        />
      );
    } else {
      return (
        <Image
                src={news.media?.[0]?.['media-metadata']?.[1]?.url}
                alt={news.media?.[0]?.['media-metadata']?.[1]?.caption}
            />
      )
    }
  }

  if (noNews) {
    return (
      <div className="App" style={{ padding: '10px' }}>
        <Spin></Spin>
        <br />
        <span className="ant-typography ant-typography-secondary">Loading...</span>
      </div>
    )
  }
  return (
    <HashRouter>
    <div className="App">
      <Layout>
      <header className="clearfix" style={{ zIndex: 1, width: '100%' }}>
        <Search placeholder="News" onChange={handleChange} style={{ width: '40%', padding: '15px' }} />
        <Search placeholder="Section" onChange={handleChangeSection} style={{ width: '40%', padding: '15px' }} />
      </header>

      <Content style={{ padding: '20px' }}>

        <>
          <span style={{ paddingRight: '10px' }}>Period</span>
          <Radio.Group style={{ paddingRight: '10px' }} onChange={handlePeriodChange}>
            <Radio.Button value={1}>Day</Radio.Button>
            <Radio.Button value={7}>Week</Radio.Button>
            <Radio.Button value={30}>Month</Radio.Button>
          </Radio.Group>
        </>

        {!noNews && news.map((news, index) => (
          <div key={index}>
            <Divider orientation="left">{news.section}</Divider>
            {CheckImage(news)}
            <br></br>
            {console.log(index)}
            {console.log(news)}
              <Link to={{pathname: '/news'}} onClick={() => storeData(news)}>
                {news.title}
              </Link>
          </div>
        ))}

      </Content>

      </Layout>
    </div>
    </HashRouter>
  );
}

export default Home;